import React, { Component } from 'react';
import BookToolBar from './bookToolBar/bookToolBar'
import BookList from './booksList/bookList'

import './App.css';

class App extends Component {

  constructor(props){
    super(props)
    this.state = {
      bookList:[],
      searchTerm:'',
      printType:'',
      bookType:'',
      error:'',
      loading:false
    }
  }



  componentDidMount(){
    this.setState({loading:true})
    const url = `https://www.googleapis.com/books/v1/volumes?q=${this.state.searchTerm}:keyes&key=AIzaSyAUoZApaq9eRmbV9OaTTHFzrLSoeXCDbsA&printType=books`
    console.log(url,'test url')
    fetch(url)
    .then(res=>{if(!res.ok){

     return res.json().then(resError=>{
        let error = resError.error.message
       return Promise.reject(error)
        })
    }
    return res.json()
  })
  .then(
    data =>{

      this.setState({
        bookList:data.items,
        error:null,
        loading:false
      })
    }
  ).catch(err=>{
    console.log('error testing final',err)
    this.setState({
      error:err,
      loading:false
      })

})

}


searchTermHandle=(e)=>{
  e.preventDefault()
  this.setState({
    searchTerm:e.target['search-tool'].value,

  })
  this.setState({loading:true,bookList:[]})
    const url = `https://www.googleapis.com/books/v1/volumes?q=${this.state.searchTerm}:keyes&key=AIzaSyAUoZApaq9eRmbV9OaTTHFzrLSoeXCDbsA&printType=books`
    console.log(url,'test url',this.state,'test state inside search')
    fetch(url)
    .then(res=>{if(!res.ok){

      return res.json().then(resError=>{
        let error = resError.error.message
        return Promise.reject(error)
        })
    }
    return res.json()
  })
  .then(
    data =>{

      this.setState({
        bookList:data.items,
        error:null,
        loading:false
      })
    }
  ).catch(err=>{
    console.log('error testing final',err)
    this.setState({
      error:err
      })

})

}
  render() {
    console.log(this.state.bookList,'test booklist')
    const error = (this.state.error)?<span>{this.state.error}</span>:(this.state.loading)?
    <span>Loading...</span>:<span></span>
    return (
      <div className="App">
        <BookToolBar searchHandle={this.searchTermHandle}/>
        <BookList books = {this.state.bookList}/>
        {error}
      </div>
    );
  }
}

export default App;
