import React, { Component } from 'react';
import BookToolBar from './bookToolBar/bookToolBar'
import BookList from './booksList/bookList'

import './App.css';

class App extends Component {

  constructor(props){
    super(props)
    this.state = {
      bookList:[],
      searchTerm:'flower',
      printType:'',
      bookType:'',
      error:''
    }
  }



  componentDidMount(){
    const url = `https://www.googleapis.com/books/v1/volumes?q=${this.state.searchTerm}:keyes&key=AIzaSyAUoZApaq9eRmbV9OaTTHFzrLSoeXCDbsA&printType=books`
    console.log(url,'test url')
    fetch(url)
    .then(res=>{if(!res.ok){

      res.json().then(resError=>{
        let error = resError.error.message
        throw new Error(error)
        })
    }
    return res.json()
  })
  .then(
    data =>{
      console.log(data.items,'test data')
      this.setState({
        bookList:data.items,
        error:null
      })
    }
  ).catch(err=>{
    console.log('error testing final',err.message)
    this.setState({
      error:err.message
      })

})

}
searchTermHandle=(e)=>{
  e.preventDefault()
  this.setState({
    searchTerm:e.target['search-tool'].value
  })

}
  render() {
    console.log(this.state.searchTerm,'test searchname')
    return (
      <div className="App">
        <BookToolBar searchHandle={this.searchTermHandle}/>
        <BookList books = {this.state.bookList}/>
        <span>{this.state.error}</span>
      </div>
    );
  }
}

export default App;
